$(function(){
    $(".read-more").readmore({

        // text for Read Less button
        hideText: "Read Less...",
      
        // text for Read More button
        readText: "Read More...",
      
        // shows text on init
        isTextShown: false,
      
        // enables fade animations
        effect: true,
      
        // animation speed
        effectOption: "fast",
      
        // button classes
        buttonClasses: "btn-primary opacity-rollover",
      
        // button ID
        id: "read-more-action"
        
      })
    })